const i=()=>{const s=document.querySelector(".header");if(!s)return;new ResizeObserver(n=>{for(let o of n){const r=o.contentRect.height;document.documentElement.style.setProperty("--header-height",`${r}px`)}}).observe(s)};class e{constructor(){this.anchors=[],this.collectAnchors(),this.addClickListeners()}static init(){return e.instance||(e.instance=new e),e.instance}collectAnchors(){this.anchors=Array.from(document.querySelectorAll('a[href^="#"]'))}addClickListeners(){this.anchors.forEach(t=>{const n=t.getAttribute("href").substring(1),o=document.getElementById(n);o&&t.addEventListener("click",r=>{r.preventDefault(),this.scrollToElement(o)})})}scrollToElement(t){const n=t.getBoundingClientRect().top+window.scrollY-window.innerHeight*.4;window.scrollTo({top:n,behavior:"smooth"})}}window.addEventListener("load",()=>{i(),e.init()});
